document.addEventListener('DOMContentLoaded',function(){const darkModeToggle=document.querySelector('.dark-mode-toggle');const html=document.documentElement;if(localStorage.getItem('theme')==='dark'){html.setAttribute('data-theme','dark');darkModeToggle.classList.add('active');}
darkModeToggle.addEventListener('click',function(){this.classList.toggle('active');if(html.getAttribute('data-theme')==='dark'){html.setAttribute('data-theme','light');localStorage.setItem('theme','light');}else{html.setAttribute('data-theme','dark');localStorage.setItem('theme','dark');}});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href');if(targetId==='#')return;const targetElement=document.querySelector(targetId);if(targetElement){targetElement.scrollIntoView({behavior:'smooth'});}});});});;